mapper.select:
select *
from request_form
where request_nickname = #{nickname}
and done_flag = #{done_flag}

select *
from request_form
where response_nickname = #{nickname} 
and done_flag = #{done_flag}

mapper.update:
update request_form set
		done_flag = 2
where rid = #{rid}

update request_form set
		done_flag = 3
where rid = #{rid}

mapper.select:
select * from notify 
where response_nickname = #{response_nickname}
and read_flag = 0

mapper.update:
update notify set
	read_flag = 1
where nid = #{nid}

update notify set
	read_flag = 1
where response_nickname = #{response_nickname}
